"use strict";
$(document).ready(function () {
    var gullUtils = {
    isMobile: function () {
        return window.matchMedia("(max-width: 767px)").matches;
    },
    changeCssLink: function (element, cssLink) {
        localStorage.setItem(element, cssLink);
        location.reload();
    }
    // Add other utility functions as needed
};
    var e = $(".search-bar input"),
        n = $(".search-close"),
        l = $(".search-ui"),
        c = $(".dropdown-sidemenu");

    e.on("focus", function () {
        l.addClass("open");
    });

    n.on("click", function () {
        l.removeClass("open");
    });

    c.find("> a").on("click", function (event) {
        event.preventDefault();
        event.stopPropagation();
        var currentDropdown = $(this).parent(".dropdown-sidemenu");
        c.not(currentDropdown).removeClass("open");
        currentDropdown.toggleClass("open");
    });

    $(".perfect-scrollbar, [data-perfect-scrollbar]").each(function () {
        var element = $(this);
        new PerfectScrollbar(this, {
            suppressScrollX: element.data("suppress-scroll-x"),
            suppressScrollY: element.data("suppress-scroll-y")
        });
    });

    $("[data-fullscreen]").on("click", function () {
        var element, requestFullScreen;
        var body = document.body;

        if (document.fullScreenElement || document.mozFullScreen || document.webkitIsFullScreen) {
            element = document;
            requestFullScreen = element.cancelFullScreen || element.webkitCancelFullScreen || element.mozCancelFullScreen || element.exitFullscreen;
            if (requestFullScreen) {
                requestFullScreen.call(element);
            } else if (window.ActiveXObject) {
                var shell = new ActiveXObject("WScript.Shell");
                shell.SendKeys("{F11}");
            }
        } else {
            element = body;
            requestFullScreen = element.requestFullScreen || element.webkitRequestFullScreen || element.mozRequestFullScreen || element.msRequestFullscreen;
            if (requestFullScreen) {
                requestFullScreen.call(element);
            } else if (window.ActiveXObject) {
                var shell = new ActiveXObject("WScript.Shell");
                shell.SendKeys("{F11}");
            }
        }
        return false;
    });

    $(window).on("load", function () {
        $("#loader").fadeOut();
        $("#preloader").delay(500).fadeOut("slow");
    });
});
